con:testCase.con:setupScript:
// read the project property "TestCases-properties"
def projectProperty = context.expand('${#Project#TestCases-properties}')

def testName = context.expand('${=testRunner.testCase.name}')

log.info "Project Property: $projectProperty"
log.info "Current Testcase: $testName"
try {
    projectProperty = projectProperty.replaceAll('\\\\', '\\\\\\\\') // Escape the escape characters

    // parse the JSON, you can use the JsonSlurper
    def jsonSlurper = new groovy.json.JsonSlurper()
    def data = jsonSlurper.parseText(projectProperty)

    // loop through the list of test case properties in  JSON data and create test case properties accordingly.
    //  first  find the matching test case property by using the id field in  JSON data:
    def currentTestCaseId = context.expand('${=testRunner.testCase.name}')
    def testCaseProperties = data.'DomiSMP-Oasis2-TestCases-properties'.find { it.id == currentTestCaseId }

    //Clean all All Current properties on testcase
    // Get all property names
    def propertyNames = testRunner.testCase.getPropertyNames()

    // Cleans current tescase properties setted
    propertyNames.each { name ->
        testRunner.testCase.removeProperty(name)
    }

    log.info "Test Properties: $testCaseProperties"

    //Iterate over the properties in the found test case properties and add them as properties to the test case
    if (testCaseProperties != null) {
        testCaseProperties.each { key, value ->
            // Skip Id property
            if (key != 'id') { 

                // Condition For loading request template property
                if (key.contains('RequestTemplate')) {
                    // Fetch the project property value
                    def templatePropertyName = value
                    def templateValue = ('${#Project#' + templatePropertyName + '}') // false disables expansion
                    testRunner.testCase.setPropertyValue(key, templateValue)
                    log.info "~Property: $key with value : $templateValue"
                //Loading the remaining properties    
                } else {
                    testRunner.testCase.setPropertyValue(key, value.toString())
                    log.info "~Property: $key with value : $value"
                }
            }
        }
    }

} catch (Exception e) {
    log.error "Error: $e"
}

